export default class Character extends React.Component {
  constructor(props){
    super(props);
  }

  onTurnEnd = () => {
    console.log(chars);
    setState({...chars, moved: true})
  }
  render(){
    return(
      <tr class="character main">
        <td><img src="" alt={`Portrait: ${props.name}`} class="portrait"/></td>
        <td><div>Name: {props.name}</div>
        <div>Initiative: {props.initiative}</div>
        <button onClick={onTurnEnd}>Done Turn</button>
        <button>Remove</button></td>
      </tr>
    )
  }
}
