import React, {useState} from 'react'


const Character = props => {
  const [chars] = useState(props)
  return(
    <tr class="character main">
      <td><img src="" alt={`Portrait: ${chars.props.name}`} class="portrait"/></td>
      <td><div>Name: {chars.props.name}</div>
      <div>Initiative: {chars.props.initiative}</div>
      <button >Done Turn</button>
      <button>Remove </button></td>
    </tr>
  )
}
export default Character;
