import React from 'react'
import Character from '../components/Character'

export default class CharacterHolder extends React.Component{
  constructor(props){
    super(props);
    this.state = {}
  }

  sortChars(){
    console.log("derpa");
    console.log(this.props);
    let orderedChars = this.props.props.sort((a,b) => {
      const aModifier = a.moved ? 0 : -99;
      const bModifier = b.moved ? 0 : -99;
      return (a.initiative - aModifier) - (b.initiative - bModifier) || (a.dex - aModifier) - (b.dex - bModifier) }).map((char) => <Character {...char}/>)
    return orderedChars
  }

  render(){
    return(
      <table><tbody>
        {console.log(this.props)}
        {this.sortChars}
      </tbody></table>
    )
  }
}
