import React from 'react'

export default class CharacterCreater extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      Initiative: null,
      Dexterity: null,
      Name: null,
      Portrait: null,
    }
  }

  handleChange(event){
    this.setState({[event.target.id]: event.target.id !== "Name" ? parseInt(event.target.value) : event.target.value })
  }



  render(){
    return(
      <form onSubmit={(e) => this.props.addChar({Name: this.state.Name,
                                                Initiative: this.state.Initiative,
                                                Dexterity: this.state.Dexterity,
                                                Portrait: this.state.Portrait})}>
        <label htmlFor="Name">
          Name:
          <input type="text" id="Name" value={this.state.name} onChange={(e) => this.handleChange(e)}/>
        </label><br/>
        <label htmlFor="Initiative">
          Initiative:
          <input type="number" id="Initiative" value={this.state.name} onInput={(e) => this.handleChange(e)}/>
        </label><br/>
        <label htmlFor="Dexterity">
          Dexterity:
          <input type="number" id="Dexterity" value={this.state.name} onInput={(e) => this.handleChange(e)}/>
        </label><br/>
          <input type="submit" value="Submit" />
      </form>
    )
  }
}
