import React, {component} from 'react'


export default class Character extends React.Component {
  constructor(props){
    super(props);
    this.state = {...props}
  }

  onTurnEnd = () => {
    console.log(this.state);
    setState({...this.state, moved: true})
  }

  render(){
    return(
      <tr class="character main">
        <td><img src="" alt={`Portrait: ${this.state.name}`} class="portrait"/></td>
        <td><div>Name: {this.state.name}</div>
        <div>Initiative: {this.state.initiative}</div>
        <button onClick={onTurnEnd}>Done Turn</button>
        <button>Remove</button></td>
      </tr>
    )
  }
}
